import React, { useEffect, useState } from 'react'
import {
    Layout,
    Menu,
    Button,
    theme,
    Flex,
    Badge,
    Dropdown,
    Drawer,
    List,
    Avatar,
} from 'antd'
import { connect, useSelector } from 'react-redux'
import {
    BarsOutlined,
    DashboardOutlined,
    LogoutOutlined,
    MenuFoldOutlined,
    MenuUnfoldOutlined,
    MessageOutlined,
    PictureOutlined,
    SettingOutlined,
    TeamOutlined,
    UnorderedListOutlined,
    UploadOutlined,
    UserOutlined,
    VideoCameraOutlined,
} from '@ant-design/icons'
import { MdOutlineNotificationsActive } from 'react-icons/md'
import { Outlet, useNavigate } from 'react-router-dom'
import axios from 'axios'
import { base_url, extractId } from '../config/constant'
const { Header, Sider, Content } = Layout
const MainPage = ({ userData }) => {
    const [collapsed, setCollapsed] = useState(false)
    const navigate = useNavigate()
    const {
        token: { colorBgContainer, borderRadiusLG },
    } = theme.useToken()
    // items for Sidebar

    const [notify, setDataNotify] = useState([])
    useEffect(() => {
        axios
            .get(
                `${base_url}/notifications/get-notifications-of-user?userid=65fafb24f990482dbe723f04`,
            )
            .then((response) => {
                setDataNotify(response.data)
            })
            .catch((error) => {
                console.error(error)
            })
    }, [])

    // items for drop down
    const items = [
        {
            key: 'profile',

            label: (
                <span
                    onClick={() => navigate('/admin/profile')}
                    style={{ textDecoration: ' none', border: 'none' }}
                >
                    <UserOutlined /> Profile
                </span>
            ),
            onclick: () => navigate('/profile'),
        },
        {
            key: 'settings',
            label: (
                <span
                    onClick={() => navigate('/admin/settings')}
                    style={{ textDecoration: ' none', border: 'none' }}
                >
                    <SettingOutlined /> Settings
                </span>
            ),
        },
        {
            key: 'logout',
            label: (
                <span
                    onClick={() => navigate('/')}
                    style={{ textDecoration: ' none', border: 'none' }}
                >
                    <LogoutOutlined /> Log out
                </span>
            ),
        },
    ]

    const [open, setOpen] = useState(false)
    const showDrawer = () => {
        setOpen(true)
    }
    const onClose = () => {
        setOpen(false)
    }

    const updateNotify = async (id) => {
        try {
            const response = await axios.put(
                `${base_url}/notifications/mark-as-read?id=${id}`,
            )
        } catch (error) {
            console.log(error)
        }
    }

    const onTap = (item) => {
        if (!item.read) updateNotify(item?.id)
        navigate(`/admin/postdetail?id=${extractId(item?.content)}`)
    }
    const [quantity, setDataQuantiy] = useState([])
    useEffect(() => {
        axios
            .get(
                `${base_url}/notifications/count?userid=65fafb24f990482dbe723f04`,
            )
            .then((response) => {
                setDataQuantiy(response.data)
            })
            .catch((error) => {
                console.error(error)
            })
    })

    useEffect(() => {
        console.log('userData:', userData)
    }, [userData])

    
    return (
        <Layout>
            <Sider trigger={null} collapsible collapsed={collapsed}>
                <div
                    style={{
                        height: 62,
                        backgroundColor: 'gray',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                    }}
                    className='demo-logo-vertical'
                >
                    <h3 style={{ color: 'white', fontSize: '18px' }}>ZNet</h3>
                </div>
                <Menu
                    theme='dark'
                    mode='inline'
                    onClick={({ key }) => {
                        if (key === 'logout') {
                        } else {
                            navigate(key)
                        }
                    }}
                    defaultSelectedKeys={['1']}
                    items={[
                        {
                            key: '/admin',
                            icon: <DashboardOutlined />,
                            label: 'Dashboard',
                        },
                        {
                            key: 'user',

                            icon: <TeamOutlined />,
                            label: 'Users',
                        },
                        {
                            key: 'post',
                            icon: <UnorderedListOutlined />,
                            label: 'Post manage',
                            children: [
                                {
                                    key: 'postlist',
                                    icon: <BarsOutlined />,
                                    label: 'List post',
                                },
                            ],
                        },
                        // {
                        //   key: "chat",
                        //   icon: <MessageOutlined />,
                        //   label: "Chat",
                        // },
                        // {
                        //   key: "settings",
                        //   icon: <SettingOutlined />,
                        //   label: "Settings",
                        // },
                    ]}
                />
            </Sider>
            <Layout>
                <Header
                    style={{
                        padding: 0,
                        background: colorBgContainer,
                        display: 'flex',
                        justifyContent: 'space-between',
                    }}
                >
                    <Button
                        type='text'
                        icon={
                            collapsed ? (
                                <MenuUnfoldOutlined />
                            ) : (
                                <MenuFoldOutlined />
                            )
                        }
                        onClick={() => setCollapsed(!collapsed)}
                        style={{
                            fontSize: '16px',
                            width: 64,
                            height: 64,
                        }}
                    />
                    <div
                        style={{
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                        }}
                    >
                        <div
                            onClick={showDrawer}
                            style={{ display: 'flex', marginRight: 20 }}
                        >
                            <Badge count={quantity.count}>
                                <MdOutlineNotificationsActive size={25} />
                            </Badge>
                        </div>
                        <Drawer
                            title='Znet notification'
                            onClose={onClose}
                            open={open}
                        >
                            <List
                                itemLayout='horizontal'
                                dataSource={notify}
                                renderItem={(item, index) => (
                                    <List.Item>
                                        <List.Item.Meta
                                            avatar={
                                                <Avatar
                                                    src={`https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQFvO1F2BSKNjmZ45H_ybXSfzFViKOkhf8sREZLB3Vuxk-jNHD9-uSIBoqvWhsYXN1xu0Q&usqp=CAU`}
                                                />
                                            }
                                            title={
                                                <a
                                                    style={{
                                                        color: item.read
                                                            ? 'grey'
                                                            : 'black',
                                                    }}
                                                    onClick={() => onTap(item)}
                                                >
                                                    {item.content}
                                                </a>
                                            }
                                        />
                                    </List.Item>
                                )}
                            />
                        </Drawer>
                        <Dropdown menu={{ items }} placement='bottom' arrow>
                            <div
                                style={{
                                    display: 'flex',
                                    alignItems: 'center',
                                }}
                            >
                                <img
                                    width={32}
                                    height={32}
                                    src='data:image/jpeg;base64,'
                                    alt=''
                                />
                                <div
                                    style={{
                                        display: 'flex',
                                        flexDirection: 'column',
                                        lineHeight: 1.5,
                                        margin: 8,
                                    }}
                                >
                                    <b className=''>{'adminInfo.name'}</b>
                                    <span className=''>
                                        {'adminInfo.email'}
                                    </span>
                                </div>
                            </div>
                        </Dropdown>
                    </div>
                </Header>
                <Content
                    style={{
                        margin: '24px 16px',
                        padding: 24,
                        minHeight: 280,
                        background: colorBgContainer,
                        borderRadius: borderRadiusLG,
                    }}
                >
                    <Outlet />
                </Content>
            </Layout>
        </Layout>
    )
}

const mapStateToProps = (state) => ({
    userData: state.auth.userData,
})

export default MainPage
